import axios from "axios";
import React, { useState } from "react";

function Search() {
  const [searched, setSearched] = useState("");
  const [texts, setText] = useState([]);
  const [ok, setOk] = useState(false);

  const handleSubmission = (e) => {
    e.preventDefault();

    axios.get("http://localhost:8080/extract-text").then((res) => {
      console.log(res);

      for (let i = 0; i < res.data.length; i++) {
        const resJson = JSON.stringify(res.data[i]);
        // console.log(resJson);
        setText((texts[i] = resJson));
      }
      // for (let i = 0; i < texts.length; i++) {
      //   console.log("MON TEXT : ", i, texts[i]);
      // }
      console.log("Variables texts : ", texts);
      // setOk(!ok);
    });
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearched(e.target.value)}
        />
        <input className="add" type="submit" />
      </form>

      {Array.isArray(texts)
        ? texts.map((element) => console.log("element"))
        : console.log("no array")}
    </div>
  );
}
export default Search;
