import axios from "axios";
import React, { useState } from "react";
import Pdf from "../pdf/Pdf";

function Search() {
  const [searched, setSearched] = useState("");
  const [texts, setText] = useState([]);
  const [focusedlist, setfocusedlist] = useState();
  const [isModify, setisModify] = useState(false);

  const handleSubmission = (e) => {
    e.preventDefault();
    if (searched !== "") {
      axios
        .get(`http://localhost:8080/extract-text/${searched}`)
        .then((res) => {
          console.log(res);
          if (res.data === []) {
            setText({ text: "Aucuns Article trouvé" });
          }
          setText(res.data);
        });
    }
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearched(e.target.value)}
        />
        <input className="add" type="submit" />
      </form>

      {texts.map((post, i) => (
        <div key={i}>
          <h1>{post.titre}</h1> {post.text}
          <br />
          <button
            value={i}
            onClick={(e) => {
              setisModify(!isModify);
              setfocusedlist(e.target.value);
            }}
          >
            Voir cet article
          </button>
          {isModify && focusedlist === `${i}` && (
            <div>
              <Pdf file={"/pdf/" + post.titre} />{" "}
            </div>
          )}
        </div>
      ))}
    </div>
  );
}
export default Search;
