import React,{useState, useEffect} from 'react'
import axios from 'axios'
import Solutions from './Solutions'

function S2({id_s1}) {

    const [symps, setSymp] = useState([])
    const [isSet, setisSet] = useState(false)
    const [focusedlist, setfocusedlist] = useState()

    const uri = `http://localhost:3001/s2/${id_s1}`

    function choixPb(e){
      setisSet(!isSet)
      setfocusedlist(e.target.value)

    }

    useEffect(() => {
      axios.get(uri)
      .then(res => {
          setSymp(res.data)
      })
      .catch(err => {console.log(err)})
      // eslint-disable-next-line react-hooks/exhaustive-deps
  },[])

  return (
   <div className="faq-list-s2">
    {symps.map((symp) => (
      <div key={symp.id_s2}>{symp.title_s2} <button className='button' value={symp.id_s2} onClick={(e) => choixPb(e)}>Choix</button>
      { isSet && focusedlist === `${symp.id_s2}` ? 
          <div>
              <Solutions id_s2={symp.id_s2}/>
          </div> 
          : null}
      </div>
    )

    )
}
</div>)
}

export default S2
