import React from "react";
import axios from "axios";
import "../styles/DataFetching.css";

export default class UpdateComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = { value: "" };
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({ value: event.target.value });
  }

  handleSubmit(event) {
    event.preventDefault();
    this.updatee(
      this.state.value,
      this.props.base,
      this.props.title,
      this.props.champ,
      this.props.id
    );
  }

  updatee(e, db, title, champ, id) {
    axios
      .put(`http://localhost:8080/update/${db}&${title}&${e}&${champ}`)
      .catch((err) => {
        console.log(err);
      });
  }

  render() {
    return (
      <form onSubmit={this.handleSubmit}>
        <input
          className="update"
          type="text"
          placeholder="Update"
          value={this.state.value}
          onChange={this.handleChange}
        />
        <input className="update-button" type="submit" value="Envoyer" />
      </form>
    );
  }
}
