import axios from "axios";
import React, { useState } from "react";
import Pdf from "../pdf/Pdf";
import SearchIcon from "@mui/icons-material/Search";
import { styled, alpha } from "@mui/material/styles";
import InputBase from "@mui/material/InputBase";

const Search = styled("div")(({ theme }) => ({
  position: "relative",
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  "&:hover": {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginLeft: 0,
  width: "100%",
  [theme.breakpoints.up("sm")]: {
    marginLeft: theme.spacing(1),
    width: "auto",
  },
}));

const SearchIconWrapper = styled("div")(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: "100%",
  position: "absolute",
  pointerEvents: "none",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: "inherit",
  "& .MuiInputBase-input": {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create("width"),
    width: "100%",
    [theme.breakpoints.up("sm")]: {
      width: "12ch",
      "&:focus": {
        width: "20ch",
      },
    },
  },
}));

function Searche() {
  const [searched, setSearched] = useState("");
  const [texts, setText] = useState([]);
  const [focusedlist, setfocusedlist] = useState();
  const [isModify, setisModify] = useState(false);
  const [defaulte, setDefaulte] = useState("");

  const handleSubmission = (e) => {
    e.preventDefault();
    if (searched !== "") {
      axios
        .get(`http://localhost:8080/extract-text/${searched}`)
        .then((res) => {
          console.log(res);
          if (res.data.length === 0) {
            setDefaulte("Aucun article trouvé...");
          } else {
            setDefaulte("");
            setText(res.data);
          }
        });
    } else {
      setDefaulte("Recherche vide.");
    }
  };

  const recherche = (e) => {
    setSearched(e.target.value);
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => recherche(e)}
        />
        <input className="" type="submit" />
      </form>

      {!defaulte ? (
        texts.map((post, i) => (
          <div key={i}>
            <h1>{post.titre}</h1> {post.text}
            <br />
            <button
              value={i}
              onClick={(e) => {
                setisModify(!isModify);
                setfocusedlist(e.target.value);
              }}
            >
              Voir cet article
            </button>
            {isModify && focusedlist === `${i}` && (
              <div>
                <Pdf file={"/pdf/" + post.titre} />{" "}
              </div>
            )}
          </div>
        ))
      ) : (
        <div>{defaulte}</div>
      )}

      <Search>
        <SearchIconWrapper>
          <SearchIcon />
        </SearchIconWrapper>
        <StyledInputBase
          placeholder="Search…"
          onChange={(e) => recherche(e)}
          inputProps={{ "aria-label": "search" }}
        />
      </Search>
    </div>
  );
}
export default Searche;
