import React, { useState, useEffect } from "react";
import axios from "axios";
import "../../styles/manager.css";

function UserManager() {
  const [users, setUsers] = useState([]);
  const [defaultroles, setDefaultRoles] = useState([]);
  const [info, setInfo] = useState(false);
  const [modify, setModify] = useState(false);
  // eslint-disable-next-line react-hooks/exhaustive-deps
  function delUser(e) {
    axios
      .post(`http://192.168.1.94:8080/api/del/user/${e.target.value}`)
      .then(() => {
        setInfo(true);
        setTimeout(() => {
          setInfo(!info);
        }, 5000);
      });
  }

  useEffect(() => {
    axios.get("http://192.168.1.94:8080/api/get/users").then((res) => {
      const tabUsers = res.data.map((item) => item);
      setUsers(tabUsers);
      console.log(users);
    });
    axios.get("http://192.168.1.94:8080/api/get/allroles").then((res) => {
      setDefaultRoles(res.data);
      console.log(defaultroles);
    });
  }, []);

  return (
    <div className="manager-container">
      <div className="manager-info">
        <div className="manager-user-info">USERS</div>
        <div className="manager-roles-info">
          <div>ROLES</div>
        </div>
        <div
          className="manager-del-user-info"
          type="button"
          placeholder="Supprimer"
        >
          Action
        </div>
      </div>
      {users.map((item, i) => (
        <div>
          {item.username === "admin" ? (
            <div className="manager">
              <div key={i} className="manager-user">
                {item.username}
              </div>
              <div className="manager-roles">
                {item.roles.map((roles) => (
                  <div key={roles.id} className="manager-roles-child">
                    {roles.name}
                  </div>
                ))}
              </div>

              <div
                key={item.id + i}
                className="manager-del-user-admin"
                type="button"
                value={item.id}
                placeholder="Supprimer"
              ></div>
            </div>
          ) : (
            <div className="manager">
              <div key={i} className="manager-user">
                {item.username}
              </div>
              <div className="manager-roles">
                {item.roles.map((roles) => (
                  <div key={roles.id} className="manager-roles-child">
                    {roles.name}
                  </div>
                ))}
              </div>

              <button
                key={item.id}
                className="manager-del-user"
                type="button"
                value={item.id}
                placeholder="Supprimer"
                onClick={(e) => {
                  delUser(e);
                }}
              >
                Supprimer
              </button>
              <button
                key={item.id}
                className="manager-mod-user"
                type="button"
                value={item.id}
                placeholder="Modifier"
                onClick={() => {
                  setModify(!modify);
                }}
              >
                Modifier
              </button>
            </div>
          )}
        </div>
      ))}

      <div>
        {defaultroles.map((role, i) => (
          <div key={role + i}> {role}</div>
        ))}
      </div>
      {info && (
        <div className="alert alert-success">
          Utilisateur supprimé avec Succès
        </div>
      )}
    </div>
  );
}

export default UserManager;
