import axios from "axios";
import React, { useState } from "react";
import Pdf from "../pdf/Pdf";

function Search() {
  const [searched, setSearched] = useState("");
  const [texts, setText] = useState([]);
  // const [isSeclected, setIsSeclected] = useState(false);
  // const STORAGEPATH = "../../../public/pdf/";
  const handleSubmission = (e) => {
    e.preventDefault();

    axios.get(`http://localhost:8080/extract-text/${searched}`).then((res) => {
      console.log(res);
      setText(res.data);
    });
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearched(e.target.value)}
        />
        <input className="add" type="submit" />
      </form>

      {texts.map((post, i) => (
        <div key={i}>
          <h1>{post.titre}</h1> {post.text}
          <Pdf file={"/" + post.text} />{" "}
        </div>
      ))}
    </div>
  );
}
export default Search;
