import React, { useState, useEffect } from "react";
import axios from "axios";
import "../../styles/manager.css";

function UserManager() {
  const [username, setUsername] = useState([]);
  const [roles, setRoles] = useState([]);

  function delUser(e) {
    console.log(e.target.value);
    axios.get(`http://192.168.1.94:8080/api/del/user/${e.target.value}`);
  }

  useEffect(() => {
    axios.get("http://192.168.1.94:8080/api/get/users").then((res) => {
      console.log("res", res);
      console.log("res.data", res.data);
      const tabUsers = res.data.map((item) => item);
      setUsername(tabUsers);
      console.log("tabUSERS", tabUsers);
    });
  }, []);

  return (
    <div className="manager-container">
      <div className="manager-info">
        <div className="manager-user-info">USERS</div>
        <div className="manager-roles-info">
          <div>ROLES</div>
        </div>
        <button
          className="manager-del-user-info"
          type="button"
          placeholder="Supprimer"
        >
          Action
        </button>
      </div>
      {username.map((item, i) => (
        <div>
          {item.username === "admin" ? (
            <div className="manager">
              <div className="manager-user" key={i}>
                {item.username}
              </div>
              <div className="manager-roles">
                {item.roles.map((roles) => (
                  <div key={roles.id} className="manager-roles-child">
                    {roles.name}
                  </div>
                ))}
              </div>

              <button
                key={item.id}
                className="manager-del-user-admin"
                type="button"
                value={item.id}
                placeholder="Supprimer"
              >
                Supprimer
              </button>
            </div>
          ) : (
            <div className="manager">
              <div className="manager-user" key={i}>
                {item.username}
              </div>
              <div className="manager-roles">
                {item.roles.map((roles) => (
                  <div key={roles.id} className="manager-roles-child">
                    {roles.name}
                  </div>
                ))}
              </div>

              <button
                key={item.id}
                className="manager-del-user"
                type="button"
                value={item.id}
                placeholder="Supprimer"
                onClick={(e) => {
                  delUser(e);
                }}
              >
                Supprimer
              </button>
            </div>
          )}
        </div>
      ))}
    </div>
  );
}

export default UserManager;
