import React, { useState } from "react";

function Search() {
  const [selectedFile, setSelectedFile] = useState();
  const [searched, setSearched] = useState("");
  const [text, setText] = useState("");

  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
  };
  const handleSubmission = (e) => {
    e.preventDefault();
    // const formData = new FormData();
    // formData.append("file", selectedFile);
    // console.log("SELECTED FILE", selectedFile);

    fetch("http://192.168.18.141:8080/extract-text", {
      method: "get",
    })
      .then((response) => {
        console.log("RESPONSE : ", response);
      })
      .then((extractedText) => {
        // console.log("MY SEARCH ", searched);
        if (
          extractedText
            .toLocaleLowerCase()
            .includes(searched.toLocaleLowerCase())
        ) {
          setText(extractedText.trim());
        } else {
          setText("");
        }
      });
  };
  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input className="add" type="file" onChange={changeHandler} />
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearched(e.target.value)}
        />
        <input className="add" type="submit" />
      </form>

      <p>{text}</p>
    </div>
  );
}
export default Search;
