import axios from "axios";
import React, { useState } from "react";

function Search() {
  const [searched, setSearched] = useState("");
  const [text, setText] = useState([]);

  const handleSubmission = (e) => {
    e.preventDefault();

    axios.get("http://localhost:8080/extract-text").then((res) => {
      for (let i = 0; i < res.data.length; i++) {
        const newText = res.data[i] + "\n";
        if (newText.includes(searched)) {
          setText([...text, newText]);
        }
      }
    });
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        {/* <input className="add" type="file" onChange={changeHandler} /> */}
        <input
          type="text"
          placeholder="Search..."
          onChange={(e) => setSearched(e.target.value)}
        />
        <input className="add" type="submit" />
      </form>
      <p>{text}</p>
    </div>
  );
}
export default Search;
