import React, { useState } from "react";
import axios from "axios";

function Uploader({ id }) {
  const [selectedFile, setSelectedFile] = useState();
  const changeHandler = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleSubmission = (e) => {
    e.preventDefault();
    // console.log(selectedFile.name);
    const formData = new FormData();
    formData.append("file", selectedFile);
    console.log("SELECTED FILE:", selectedFile);
    axios
      .post("http://localhost:8080/upload/pdf/", formData)
      .then((e) => {
        console.log("Success", e);
        axios
          .post(`http://localhost:8080/solutions/${id}&${e.data.originalname}`)
          .catch((err) => {
            console.log("ERREUR UPDATE NAME DATABASE", err);
          });
      })
      .catch((e) => {
        console.error("Error-UPDATER", e);
      });
  };

  return (
    <div>
      <form method="POST" action="#" id="#" onSubmit={handleSubmission}>
        <input className="add" type="file" onChange={changeHandler} />
        <input className="add" type="submit" />
      </form>
    </div>
  );
}

export default Uploader;
