import axios from 'axios'
import '../styles/DataFetching.css'
import S1 from './S1'


export default class DataFetching extends Component {
  constructor(props) {
    super(props);
    this.state = {
      content: ""
    };

  }

  componentDidMount() {
    const [isSet, setisSet] = useState(false)
    const [focusedlist, setfocusedlist] = useState()
    const [posts, setPosts] = useState([])

    useEffect(() => {
        axios.get('http://localhost:3001/pb/')
        .then(res => {
            setPosts(res.data)
        })
        .catch(err => {console.log(err)})
    },[])

    function choixPb(e){
      setisSet(!isSet)
      setfocusedlist(e.target.value)

    }
  }
    render() {
    return (
   <div>
      <ul className='faq-list-pb'>
       {
       posts.map(({id,  title_pb}) => (
         <div className='faq-item-pb'> {title_pb} <button className='button' value={id} onClick={(e) => choixPb(e)}>Choix</button>
          { isSet && focusedlist === `${id}` ? 
          <div>
              <S1 id={id}  />
          </div> 
          : null}
        </div>
       )
       )
       }
    </ul>
  </div>
  );
}
}
