import React,{useState, useEffect} from 'react'
import axios from 'axios'
import '../styles/DataFetching.css'
import S1 from './S1'

function DataFetching() {
    const [isSet, setisSet] = useState(false)
    const [focusedlist, setfocusedlist] = useState()
    const [posts, setPosts] = useState([])

    useEffect(() => {
        axios.get('http://localhost:8080/pb/')
        .then(res => {
            setPosts(res.data)
        })
        .catch(err => {console.log(err)})
    },[])

    function choixPb(e){
      setisSet(!isSet)
      setfocusedlist(e.target.value)

    }

    return (
   <div>
      <ul className='faq-list-pb'>
       {
       posts.map(({id,  title_pb}) => (
         <div className='faq-item-pb'> {title_pb} <button className='button' value={id} onClick={(e) => choixPb(e)}>Choix</button>
          { isSet && focusedlist === `${id}` ? 
          <div>
              <S1 id={id}  />
          </div> 
          : null}
        </div>
       )
       )
       }
    </ul>
  </div>
  )
}

export default DataFetching
